import { Component, OnInit } from '@angular/core';
import { Project } from '../../models/project';
import { ProjectService } from '../../services/project.service'
import { UploadService } from '../../services/upload.service';
import { Global } from '../../services/global';
import { Router,ActivatedRoute,Params } from '@angular/router';
import { NgForm } from '@angular/forms';
@Component({
  selector: 'app-edit',
  templateUrl: '../create/create.component.html',
  styleUrls: ['./edit.component.css'],
  providers: [ProjectService, UploadService]
})

export class EditComponent implements OnInit {
  public title: string;
  public project!: Project;
  public status!: string;
  public filesToUpload!: Array<File>;
  public save_project!: string;
  public url: string;

  constructor(
    private _projectService: ProjectService,
    private _uploadService: UploadService,
    private _router: Router,
    private _route: ActivatedRoute
  ) { 
    this.title = "Editar proyecto";
    this.url = Global.url;
  }
  ngOnInit(): void {
    this._route.params.subscribe(params => {
      let id = params.id;
      this.getProject(id);
    });
  }
  getProject(id: any){
    this._projectService.getProject(id).subscribe(
        response => {
            this.project = response.project;
        },
        error => {
          console.log(<any>error);
        }
      );
  }
  onSubmit(form: NgForm){
    this._projectService.updateProject(this.project).subscribe(
        response => {
          if(response.project){       
          // Subir la imagen
          if(this.filesToUpload){
            this._uploadService.makeFileRequest(Global.url + "upload-image/"+response.project._id, [], this.filesToUpload, 'image').then((result: any) => {
              this.save_project = result; //result.project
              this.status = 'success';
            });
          }else{
            this.save_project = response.project; //result.project
            this.status = 'success';
          }
        }else{
          this.status = 'failed';
        }
        },
        error => {
          console.log(<any>error);
        }
    );
  }
  fileChangeEvent(fileInput: any){
     this.filesToUpload = <Array<File>>fileInput.target.files;
  }
}
<div class="container" *ngIf="project">
	<h2>{{title}}</h2>

	<div class="message success" *ngIf="status == 'success'">
		El proyecto se ha creado correctamente, puedes <a [routerLink]="['/proyecto',project._id]">verlo aqui</a>
	</div>
	<div class="message failed" *ngIf="status == 'failed'">
		El proyecto no se ha podido crear
	</div>

	<form #projectForm="ngForm" (ngSubmit)="onSubmit(projectForm)">
		<p>
			<label for="name">Nombre</label>
			<input type="text" name="name" #name="ngModel" [(ngModel)]="project.name" required />
			<span *ngIf="name.touched && !name.valid" class="form_error">
				El nombre es obligatorio
			</span>
		</p>

		<p>
			<label for="description">Descripcion</label>
			<textarea name="description" #description="ngModel" [(ngModel)]="project.description" required ></textarea>
			<span *ngIf="description.touched && !description.valid" class="form_error">
				La descripción es obligatoria
			</span>
		</p>
		<p>
			<label for="category">Categoria</label>
			<input type="text" name="category" #category="ngModel" [(ngModel)]="project.category" required />
			<span *ngIf="category.touched && !category.valid" class="form_error">
				El categoria es obligatoria
			</span>
		</p>
		<p>
			<label for="year">Año de lanzamiento</label>
			<input type="number" name="year" #year="ngModel" [(ngModel)]="project.year" />
		</p>
		<p>
			<label for="langs">Lenguajes utilizados</label>
			<input type="text" name="langs" #langs="ngModel" [(ngModel)]="project.langs" required />
			<span *ngIf="langs.touched && !langs.valid" class="form_error">
				Los lenguajes son obligatorio
			</span>
		</p>
		<p>
			<label for="image">Imagen del proyecto</label>
			<span class="image" *ngIf="project.image" style="float: none">
				<img src="{{url+'get-image/'+project.image}}"  style="width: 100px;" >
			</span><br>
			<input type="file" name="image" placeholder="subir imagen" (change)="fileChangeEvent($event)" />
		</p>

		<input type="submit" value="Enviar" [disabled]="!projectForm.form.valid"/>
	</form>

</div>
